mapper.update:
UPDATE device

    is_cancel = #{isCancel},
    update_time = #{updateTime}

WHERE device_id = #{deviceId}
  and is_deleted = 0

UPDATE device

    is_deleted = 1,
    update_time = #{updateTime}

WHERE device_id = #{deviceId}
  and is_deleted = 0

mapper.select:
SELECT * FROM device
         WHERE user_id = #{userId}
           and finger_printing = #{fingerPrinting}
           and is_deleted = 0

SELECT user_id FROM device
         WHERE device_id = #{deviceId}
           and is_deleted = 0

select device_id,device_name,update_time,ip,create_time from device
where user_id = #{userId} and is_deleted = 0

    and  device_id > #{lastDeviceId}

and is_cancel = 1
order by device_id
limit #{limit}

select count(1) from device
where user_id = #{userId} and is_deleted = 0

    and  device_id > #{lastDeviceId}

order by device_id
limit #{limit}
