mapper.update:
UPDATE device

    is_cancel = #{isCancel},
    update_time = #{updateTime}

WHERE device_id = #{deviceId}
  and is_deleted = 0

UPDATE device

    is_deleted = 1,
    update_time = #{updateTime}

WHERE device_id = #{deviceId}
  and is_deleted = 0

mapper.select:
SELECT * FROM device
         WHERE user_id = #{userId}
           and ip = #{ip}
           and is_deleted = 0
