mapper.select:
select id,position_id,position_name,level,subordinate,team_id, create_by,create_time,update_by,update_time from position where subordinate = #{positionId}
select id,position_id,position_name,level,subordinate,team_id, create_by,create_time,update_by,update_time from position where subordinate in
#{position.positionId}
and is_deleted = 0
select t1.id,t1.position_id,t1.position_name,t1.level,t1.subordinate,t1.team_id, t1.create_by,t1.create_time,t1.update_by,t1.update_time from position as t1, user_position as t2 where t2.user_id = #{userId} and t1.position_id = t2.position_id and t1.is_deleted = 0 and t2.is_deleted = 0
